-- 1. MANZANILLA
INSERT INTO plants (name, scientific_name, description, category, properties, tags, ailments, usage_instructions, warnings, therapeutic_indications, contraindications, side_effects, drug_interactions, dosage_adults, dosage_children, administration_route, preparation_method, safe_pregnancy, safe_lactation, safe_children, evidence_level, clinical_studies, nursing_notes, monitoring_parameters, scientific_article_url)
VALUES ('Manzanilla', 'Matricaria chamomilla', 'Planta medicinal con propiedades antiinflamatorias, antiespasmódicas y sedantes suaves. Sus flores contienen aceites esenciales con apigenina.', 'Trastornos Gastrointestinales', 'Antiinflamatoria, antiespasmódica, sedante suave, carminativa', ARRAY['Gastritis', 'Dispepsia', 'Náuseas', 'Cólicos abdominales', 'Insomnio', 'Ansiedad', 'Nerviosismo', 'Estrés'], ARRAY['insomnio', 'ansiedad', 'gastritis', 'cólicos'], 'Infusión: 1-2 cucharaditas en 150ml agua caliente, reposar 5-10 minutos tapado. Tomar 3-4 veces al día.', 'Evitar con alergia a Asteraceae. Suspender 2 semanas antes de cirugías.', 'Trastornos gastrointestinales, ansiedad leve, trastornos del sueño, inflamación de mucosas.', 'Hipersensibilidad a Asteraceae. Alergia al polen.', 'Reacciones alérgicas, raramente vómitos. Somnolencia en dosis altas.', 'Potencia sedantes y anticoagulantes. Aumenta riesgo de sangrado con AINEs.', '150ml infusión 3-4 veces/día. Tintura: 15-30 gotas 3 veces/día.', 'Mayores 6 meses: 30-60ml diluida 2-3 veces/día. Mayores 5 años: 100ml.', ARRAY['oral', 'topica'], 'Infusión: 2-3g flores en 150ml agua 90°C, reposar 5-10 min tapado.', true, true, true, 'Alto', 'Srivastava JK, et al. Mol Med Rep. 2010;3(6):895-901.', 'Monitorear respuesta terapéutica. Observar signos de alergia.', ARRAY['signos de alergia', 'calidad del sueño'], 'https://pubmed.ncbi.nlm.nih.gov/22228617/');

-- 2. JENGIBRE
INSERT INTO plants (name, scientific_name, description, category, properties, tags, ailments, usage_instructions, warnings, therapeutic_indications, contraindications, side_effects, drug_interactions, dosage_adults, dosage_children, administration_route, preparation_method, safe_pregnancy, safe_lactation, safe_children, evidence_level, clinical_studies, nursing_notes, monitoring_parameters, scientific_article_url)
VALUES ('Jengibre', 'Zingiber officinale', 'Raíz con propiedades antiinflamatorias y antieméticas. Contiene gingeroles y shogaoles.', 'Trastornos Gastrointestinales', 'Antiemético, antiinflamatorio, antioxidante, digestivo', ARRAY['Náuseas', 'Vómitos', 'Dispepsia', 'Gastritis', 'Artritis', 'Osteoartritis', 'Dolor articular', 'Inflamación crónica'], ARRAY['náuseas', 'vómitos', 'mareos', 'artritis'], 'Para náuseas: 1g fresco o 250mg polvo 4 veces/día. Para inflamación: 2-4g diarios.', 'Evitar >4g/día en embarazo. Suspender 1 semana antes de cirugías.', 'Náuseas gestacionales, osteoartritis, dispepsia funcional.', 'Alergia al jengibre. Cálculos biliares activos. Evitar dosis altas en embarazo.', 'Acidez, eructos. Raramente diarrea. Puede aumentar sangrado.', 'Potencia anticoagulantes. Interacción con antidiabéticos.', 'Polvo: 250mg-1g 4 veces/día (máx 4g). Fresco: 2-4g diarios.', 'Mayores 2 años: 1/4 dosis adulto. Mayores 6 años: 1/2 dosis.', ARRAY['oral'], 'Infusión: rallar 2-3g rizoma fresco, 250ml agua hirviendo, 10 min.', true, true, true, 'Alto', 'Viljoen E, et al. Nutr J. 2014;13:20.', 'Monitorear glucemia en diabéticos. Suspensión pre-quirúrgica.', ARRAY['glucemia', 'tiempo coagulación'], 'https://pubmed.ncbi.nlm.nih.gov/24642205/');

-- 3. MENTA
INSERT INTO plants (name, scientific_name, description, category, properties, tags, ailments, usage_instructions, warnings, therapeutic_indications, contraindications, side_effects, drug_interactions, dosage_adults, dosage_children, administration_route, preparation_method, safe_pregnancy, safe_lactation, safe_children, evidence_level, clinical_studies, nursing_notes, monitoring_parameters, scientific_article_url)
VALUES ('Menta', 'Mentha piperita', 'Planta aromática con propiedades digestivas. El mentol relaja músculo liso intestinal.', 'Síndrome del Intestino Irritable', 'Antiespasmódico, carminativo, digestivo, analgésico', ARRAY['Síndrome del intestino irritable', 'Dispepsia', 'Flatulencia', 'Meteorismo', 'Cólicos abdominales', 'Náuseas'], ARRAY['síndrome colon irritable', 'dispepsia', 'gases'], 'Cápsulas entéricas: 0.2-0.4ml aceite 3 veces/día entre comidas.', 'NO aceite puro en bebés (laringoespasmo). Evitar en reflujo severo.', 'Síndrome intestino irritable, dispepsia, flatulencia, náuseas.', 'Reflujo severo. Cálculos biliares. Hernia hiatal. Niños <3 años.', 'Acidez en susceptibles. Reacciones alérgicas. Raramente cefalea.', 'Reduce absorción hierro. Inhibe CYP3A4. Evitar con antiácidos.', 'Cápsulas: 0.2-0.4ml 3 veces/día. Infusión: 1 cucharada en 200ml.', 'Mayores 8 años: infusión suave 50-100ml después comidas.', ARRAY['oral', 'topica', 'inhalacion'], 'Infusión: 1 cda hojas en 200ml agua 85-90°C, reposar 5-7 min tapado.', true, false, true, 'Alto', 'Khanna R, et al. J Clin Gastroenterol. 2014;48(6):505-512.', 'Cápsulas entéricas para colon irritable. No aceite puro en niños.', ARRAY['síntomas GI', 'dolor abdominal'], 'https://pubmed.ncbi.nlm.nih.gov/24100754/');

-- 4. HINOJO
INSERT INTO plants (name, scientific_name, description, category, properties, tags, ailments, usage_instructions, warnings, therapeutic_indications, contraindications, side_effects, drug_interactions, dosage_adults, dosage_children, administration_route, preparation_method, safe_pregnancy, safe_lactation, safe_children, evidence_level, clinical_studies, nursing_notes, monitoring_parameters, scientific_article_url)
VALUES ('Hinojo', 'Foeniculum vulgare', 'Planta carminativa con anetol que relaja músculo liso gastrointestinal. Tradicionalmente usada para cólicos infantiles.', 'Cólicos y Flatulencia', 'Carminativo, antiespasmódico, galactagogo, expectorante', ARRAY['Cólicos abdominales', 'Flatulencia', 'Meteorismo', 'Dispepsia', 'Bronquitis aguda', 'Tos productiva'], ARRAY['cólicos lactantes', 'gases', 'dispepsia', 'bronquitis'], 'Infusión: 1-2g semillas machacadas en 150ml agua, 3 veces/día después comidas. Para lactancia: 3-4 veces/día.', 'Evitar aceite esencial en embarazo y epilepsia. Precaución en cáncer hormonodependiente.', 'Dispepsia, flatulencia, cólicos infantiles, estimular lactación, tos productiva.', 'Alergia a Apiáceas. Cáncer mama/útero (efecto estrogénico). Epilepsia (aceite).', 'Reacciones alérgicas. Raramente fotodermatitis. Aceite: neurotoxicidad.', 'Posible interacción con anticonceptivos (efecto estrogénico). Ciprofloxacina.', 'Infusión: 1-2g semillas 3 veces/día. Tintura: 2-4ml 3 veces/día.', 'Lactantes >1 mes: 5-10ml infusión diluida para cólicos 2-3 veces/día.', ARRAY['oral'], 'Infusión: machacar 1-2g semillas, agua 90°C, reposar 10 min tapado.', false, true, true, 'Moderado', 'Alexandrovich I, et al. Altern Ther Health Med. 2003;9(4):58-61.', 'Machacar semillas antes de infusión. Efectivo para cólicos lactantes.', ARRAY['cólicos infantiles', 'producción leche'], 'https://pubmed.ncbi.nlm.nih.gov/12868253/');

-- 5. ANÍS
INSERT INTO plants (name, scientific_name, description, category, properties, tags, ailments, usage_instructions, warnings, therapeutic_indications, contraindications, side_effects, drug_interactions, dosage_adults, dosage_children, administration_route, preparation_method, safe_pregnancy, safe_lactation, safe_children, evidence_level, clinical_studies, nursing_notes, monitoring_parameters, scientific_article_url)
VALUES ('Anís', 'Pimpinella anisum', 'Semillas aromáticas con anetol, efectivo carminativo y expectorante. Reduce gases intestinales.', 'Flatulencia y Dispepsia', 'Carminativo, antiespasmódico, expectorante, galactagogo', ARRAY['Flatulencia', 'Dispepsia', 'Cólicos abdominales', 'Bronquitis aguda', 'Tos productiva', 'Meteorismo'], ARRAY['flatulencia', 'cólicos', 'bronquitis', 'dispepsia'], 'Infusión: 1-2 cucharaditas semillas machacadas después comidas. Para tos: 3-4 veces/día con miel.', 'Evitar aceite esencial puro. Precaución en cáncer hormonodependiente.', 'Flatulencia, dispepsia, cólicos, bronquitis, estimular lactación.', 'Alergia a Apiáceas. Cáncer mama/útero. Epilepsia (aceite esencial).', 'Reacciones alérgicas cutáneas. Raramente náuseas. Aceite: convulsiones.', 'Posible interacción hormonal con anticonceptivos. Anticoagulantes.', 'Infusión: 1-2 cucharaditas 3 veces/día. Tintura: 2-3ml 3 veces/día.', 'Mayores 1 año: infusión muy diluida (10-20ml) para gases.', ARRAY['oral'], 'Infusión: machacar semillas, 1-2 cditas en 200ml agua 85°C, 10 min.', false, true, true, 'Moderado', 'Shojaii A, Abdollahi Fard M. J Ethnopharmacol. 2012;141(1):446-450.', 'Siempre machacar semillas antes de infusión para liberar aceites.', ARRAY['síntomas digestivos', 'tos'], 'https://pubmed.ncbi.nlm.nih.gov/22366675/');

-- 6. BOLDO
INSERT INTO plants (name, scientific_name, description, category, properties, tags, ailments, usage_instructions, warnings, therapeutic_indications, contraindications, side_effects, drug_interactions, dosage_adults, dosage_children, administration_route, preparation_method, safe_pregnancy, safe_lactation, safe_children, evidence_level, clinical_studies, nursing_notes, monitoring_parameters, scientific_article_url)
VALUES ('Boldo', 'Peumus boldus', 'Hojas con boldina, alcaloide colerético y hepatoprotector. Estimula secreción biliar.', 'Trastornos Hepatobiliares', 'Colerético, hepatoprotector, digestivo, diurético leve', ARRAY['Dispepsia', 'Insuficiencia biliar', 'Insuficiencia digestiva', 'Cálculos biliares', 'Discinesia biliar', 'Hígado graso'], ARRAY['dispepsia', 'insuficiencia biliar', 'digestiones lentas'], 'Infusión: 1-2g hojas antes comidas principales. Máximo 2-3 tazas/día, no más de 4 semanas continuas.', 'Contraindicado en obstrucción biliar y hepatopatía severa. No usar >4 semanas.', 'Dispepsia hepatobiliar, insuficiencia biliar leve, digestiones pesadas.', 'Obstrucción vías biliares. Cálculos grandes. Hepatopatía severa. Embarazo/lactancia.', 'Diarrea, náuseas en dosis altas. Raramente reacciones alérgicas.', 'Puede potenciar anticoagulantes. Interacción con diuréticos.', 'Infusión: 1-2g hojas 2-3 veces/día antes comidas. Máximo 4 semanas.', 'No recomendado en niños menores de 12 años.', ARRAY['oral'], 'Infusión: 1-2g hojas secas en 200ml agua hirviendo, reposar 10 min.', false, false, false, 'Moderado', 'Speisky H, Cassels BK. Pharmacol Res. 1994;29(1):1-12.', 'No exceder 4 semanas uso continuo. Contraindicado en obstrucción biliar.', ARRAY['función hepática', 'síntomas digestivos'], 'https://pubmed.ncbi.nlm.nih.gov/8202687/');

-- 7. ALCACHOFA
INSERT INTO plants (name, scientific_name, description, category, properties, tags, ailments, usage_instructions, warnings, therapeutic_indications, contraindications, side_effects, drug_interactions, dosage_adults, dosage_children, administration_route, preparation_method, safe_pregnancy, safe_lactation, safe_children, evidence_level, clinical_studies, nursing_notes, monitoring_parameters, scientific_article_url)
VALUES ('Alcachofa', 'Cynara scolymus', 'Hojas con cinarina, sustancia hepatoprotectora y colerética. Reduce colesterol y protege hígado.', 'Hipercolesterolemia y Hepatoprotección', 'Hepatoprotector, colerético, hipolipemiante, digestivo', ARRAY['Colesterol alto', 'Triglicéridos elevados', 'Dispepsia', 'Hígado graso', 'Insuficiencia hepática', 'Insuficiencia digestiva'], ARRAY['dispepsia', 'hipercolesterolemia', 'hígado graso'], 'Extracto: 300-640mg 3 veces/día antes comidas. Infusión: 2-3 tazas/día.', 'Evitar en obstrucción biliar. Puede causar flatulencia inicial.', 'Dispepsia, hipercolesterolemia leve, hepatoprotección, hígado graso.', 'Obstrucción vías biliares. Cálculos biliares. Alergia a Asteraceae.', 'Flatulencia, diarrea leve. Raramente reacciones alérgicas.', 'Puede potenciar hipolipemiantes. Interacción con anticoagulantes.', 'Extracto seco: 300-640mg 3 veces/día. Infusión: 2-3g hojas 3 veces/día.', 'Mayores 12 años: 150-300mg extracto 2 veces/día bajo supervisión.', ARRAY['oral'], 'Infusión: 2-3g hojas secas en 200ml agua hirviendo, 10-15 min.', false, false, false, 'Alto', 'Wider B, et al. Cochrane Database Syst Rev. 2009;(4):CD003335.', 'Monitorear perfil lipídico. Puede tardar 6-8 semanas efecto completo.', ARRAY['colesterol', 'transaminasas', 'síntomas digestivos'], 'https://pubmed.ncbi.nlm.nih.gov/19821327/');

-- 8. PAPAYA
INSERT INTO plants (name, scientific_name, description, category, properties, tags, ailments, usage_instructions, warnings, therapeutic_indications, contraindications, side_effects, drug_interactions, dosage_adults, dosage_children, administration_route, preparation_method, safe_pregnancy, safe_lactation, safe_children, evidence_level, clinical_studies, nursing_notes, monitoring_parameters, scientific_article_url)
VALUES ('Papaya', 'Carica papaya', 'Fruto con papaína, enzima proteolítica que mejora digestión de proteínas. Hojas con propiedades antioxidantes.', 'Dispepsia e Insuficiencia Digestiva', 'Digestivo, proteolítico, cicatrizante, antioxidante', ARRAY['Dispepsia', 'Insuficiencia digestiva', 'Gastritis', 'Heridas superficiales', 'Estreñimiento'], ARRAY['dispepsia', 'digestión lenta', 'heridas'], 'Fruto maduro: consumir con comidas ricas en proteínas. Suplemento papaína: 500-1000mg con comidas.', 'Látex verde puede causar alergia. Evitar hojas en embarazo (efecto uterotónico).', 'Dispepsia, insuficiencia enzimática, digestión lenta de proteínas.', 'Alergia al látex. Embarazo (hojas y fruto verde). Anticoagulación activa.', 'Reacciones alérgicas. Raramente náuseas, diarrea con dosis altas.', 'Puede potenciar anticoagulantes. Aumenta absorción algunos antibióticos.', 'Papaína: 500-1000mg con cada comida principal.', 'Mayores 3 años: fruto maduro como alimento. No suplementos sin supervisión.', ARRAY['oral', 'topica'], 'Consumir fruto maduro fresco. Infusión hojas: 1-2g en 200ml agua (solo adultos).', false, true, true, 'Moderado', 'Ketnawa S, et al. Food Sci Biotechnol. 2019;28(1):17-30.', 'Fruto maduro seguro. Evitar látex y hojas en embarazo.', ARRAY['síntomas digestivos', 'alergias'], 'https://pubmed.ncbi.nlm.nih.gov/30815302/');

-- 9. CÚRCUMA
INSERT INTO plants (name, scientific_name, description, category, properties, tags, ailments, usage_instructions, warnings, therapeutic_indications, contraindications, side_effects, drug_interactions, dosage_adults, dosage_children, administration_route, preparation_method, safe_pregnancy, safe_lactation, safe_children, evidence_level, clinical_studies, nursing_notes, monitoring_parameters, scientific_article_url)
VALUES ('Cúrcuma', 'Curcuma longa', 'Rizoma con curcumina, potente antiinflamatorio y antioxidante. Protege hígado y mejora digestión.', 'Inflamación Crónica y Hepatoprotección', 'Antiinflamatorio, antioxidante, hepatoprotector, colerético', ARRAY['Inflamación crónica', 'Artritis', 'Osteoartritis', 'Dolor articular', 'Dispepsia', 'Hígado graso', 'Enfermedades inflamatorias crónicas'], ARRAY['artritis', 'dispepsia', 'inflamación', 'hígado graso'], 'Con comidas grasas para mejor absorción. Extracto: 500-1000mg 2-3 veces/día. Añadir pimienta negra (piperina) para aumentar absorción 2000%.', 'Evitar dosis altas en embarazo. Suspender 2 semanas antes cirugías. Puede manchar ropa.', 'Osteoartritis, dispepsia, inflamación crónica, hepatoprotección.', 'Obstrucción biliar. Cálculos biliares. Úlcera péptica activa. Embarazo (dosis altas).', 'Molestias GI leves, náuseas. Raramente diarrea. Puede causar manch as amarillas.', 'Potencia anticoagulantes. Puede reducir glucosa (monitorear en diabéticos).', 'Extracto estandarizado 95% curcumina: 500-1000mg 2-3 veces/día con comidas.', 'Mayores 12 años: 250-500mg/día con comidas bajo supervisión.', ARRAY['oral'], 'Polvo: 1-3g con alimentos. Leche dorada: 1 cdita polvo + leche + pimienta.', false, true, true, 'Alto', 'Daily JW, et al. J Med Food. 2016;19(8):717-729.', 'Tomar con pimienta negra y grasas. Puede manchar dientes temporalmente.', ARRAY['marcadores inflamación', 'síntomas articulares'], 'https://pubmed.ncbi.nlm.nih.gov/27533649/');

-- 10. REGALIZ
INSERT INTO plants (name, scientific_name, description, category, properties, tags, ailments, usage_instructions, warnings, therapeutic_indications, contraindications, side_effects, drug_interactions, dosage_adults, dosage_children, administration_route, preparation_method, safe_pregnancy, safe_lactation, safe_children, evidence_level, clinical_studies, nursing_notes, monitoring_parameters, scientific_article_url)
VALUES ('Regaliz', 'Glycyrrhiza glabra', 'Raíz con glicirricina, antiinflamatoria y protectora mucosa gástrica. Efecto expectorante.', 'Úlcera Péptica y Gastritis', 'Antiinflamatorio, demulcente, expectorante, antiulceroso', ARRAY['Úlcera gástrica', 'Úlcera duodenal', 'Gastritis', 'Tos productiva', 'Faringitis', 'Bronquitis aguda'], ARRAY['gastritis', 'úlcera péptica', 'tos', 'faringitis'], 'Infusión: 1-2g raíz 3 veces/día. Deglicirrinizado (DGL): 380-760mg antes comidas. NO usar más de 4-6 semanas sin supervisión.', 'NO usar largo plazo (>6 semanas). Eleva presión arterial. Reduce potasio. Contraindicado en hipertensión no controlada.', 'Gastritis, úlcera péptica, faringitis, bronquitis, tos productiva.', 'Hipertensión no controlada. Insuficiencia renal. Hipopotasemia. Embarazo. Enfermedad cardiaca.', 'Hipertensión, hipopotasemia, retención líquidos, cefalea. Edema en uso prolongado.', 'Aumenta efecto diuréticos, digital, corticoides. Reduce eficacia antihipertensivos.', 'Raíz: 1-2g 3 veces/día máximo 6 semanas. DGL: 380-760mg antes comidas.', 'No recomendado en menores de 12 años salvo bajo supervisión médica.', ARRAY['oral'], 'Infusión: 1-2g raíz en 200ml agua, hervir 10 min. Masticar raíz para garganta.', false, false, false, 'Alto', 'Aly AM, et al. J Evid Based Complementary Altern Med. 2005;2(4):523-532.', 'Monitorear PA y potasio. Preferir DGL para gastritis (sin efectos HTA).', ARRAY['presión arterial', 'potasio', 'edema'], 'https://pubmed.ncbi.nlm.nih.gov/16322819/');

-- ====================================================================
-- PLANTAS RELAJANTES Y PARA EL SUEÑO (11-20)
-- ====================================================================

-- 11. LAVANDA
INSERT INTO plants (name, scientific_name, description, category, properties, tags, ailments, usage_instructions, warnings, therapeutic_indications, contraindications, side_effects, drug_interactions, dosage_adults, dosage_children, administration_route, preparation_method, safe_pregnancy, safe_lactation, safe_children, evidence_level, clinical_studies, nursing_notes, monitoring_parameters, scientific_article_url)
VALUES ('Lavanda', 'Lavandula angustifolia', 'Planta aromática con linalool que modula GABA. Propiedades ansiolíticas y sedantes.', 'Trastornos de Ansiedad', 'Ansiolítico, sedante suave, antiespasmódico, antiséptico', ARRAY['Ansiedad', 'Insomnio', 'Estrés', 'Cefalea tensional', 'Nerviosismo', 'Irritabilidad'], ARRAY['ansiedad', 'insomnio', 'estrés', 'dolor de cabeza'], 'Aromaterapia: 3-5 gotas aceite en difusor 15-30 min antes dormir. Oral: infusión 2-3 veces/día. Tópico: aceite al 2-5% en masajes.', 'No ingerir aceite puro (tóxico). Puede causar somnolencia. Diluir antes de aplicar.', 'Ansiedad generalizada leve-moderada, insomnio de conciliación, cefalea tensional.', 'Alergia a Lamiaceae. No aceite puro en embarazo. Epilepsia sin supervisión.', 'Somnolencia, raramente cefalea. Dermatitis con aceite sin diluir.', 'Potencia sedantes, benzodiacepinas y alcohol. Aumenta somnolencia antihistamínicos.', 'Infusión: 150-200ml 2-3 veces/día. Aromaterapia: 3-5 gotas. Baño: 5-10 gotas.', 'Mayores 3 años: aromaterapia supervisada 1-2 gotas, sesiones 5-10 min.', ARRAY['oral', 'topica', 'inhalacion'], 'Infusión: 1-2 cditas flores en 200ml agua 85°C, reposar 10 min tapado.', false, true, true, 'Moderado', 'Koulivand PH, et al. Evid Based Complement Alternat Med. 2013;2013:681304.', 'No conducir después de uso. Enseñar dilución aceite (nunca puro).', ARRAY['calidad sueño', 'ansiedad', 'somnolencia diurna'], 'https://pubmed.ncbi.nlm.nih.gov/24063515/');

-- 12. VALERIANA
INSERT INTO plants (name, scientific_name, description, category, properties, tags, ailments, usage_instructions, warnings, therapeutic_indications, contraindications, side_effects, drug_interactions, dosage_adults, dosage_children, administration_route, preparation_method, safe_pregnancy, safe_lactation, safe_children, evidence_level, clinical_studies, nursing_notes, monitoring_parameters, scientific_article_url)
VALUES ('Valeriana', 'Valeriana officinalis', 'Raíz con ácido valerénico que aumenta GABA. Sedante herbal más estudiado.', 'Insomnio y Trastornos del Sueño', 'Sedante, hipnótico, ansiolítico, relajante muscular', ARRAY['Insomnio', 'Trastornos del sueño', 'Ansiedad', 'Nerviosismo', 'Estrés', 'Dolor muscular'], ARRAY['insomnio', 'ansiedad', 'nerviosismo', 'tensión muscular'], 'Tomar 400-900mg extracto 30-60 min antes dormir. Efecto completo requiere 2-4 semanas. No >3 meses sin supervisión.', 'NO conducir después. Evitar alcohol. No en embarazo/lactancia. Suspender 2 semanas antes cirugías.', 'Insomnio crónico, ansiedad generalizada, agitación nerviosa, tensión muscular.', 'Embarazo/lactancia. Niños <12 años. Hepatopatía. Depresión mayor.', 'Somnolencia diurna, mareo, cefalea. Raramente hepatotoxicidad >2 meses.', 'Potencia significativamente sedantes, benzodiacepinas, alcohol. Interacción antidepresivos.', 'Extracto (0.8% valerénico): 400-900mg antes dormir. Ansiedad: 300-600mg/día.', 'No en <12 años. Adolescentes >12: 200-300mg dosis bajas bajo supervisión.', ARRAY['oral'], 'Infusión: 2-3g raíz triturada en 200ml agua hirviendo, 10-15 min tapado.', false, false, false, 'Alto', 'Bent S, et al. Am J Med. 2006;119(12):1005-1012.', 'Advertir NO conducir. Monitorear hepatotoxicidad si >2 meses. Tarda 2-4 semanas.', ARRAY['calidad sueño', 'función hepática', 'depresión'], 'https://pubmed.ncbi.nlm.nih.gov/17145239/');

-- 13. PASIFLORA
INSERT INTO plants (name, scientific_name, description, category, properties, tags, ailments, usage_instructions, warnings, therapeutic_indications, contraindications, side_effects, drug_interactions, dosage_adults, dosage_children, administration_route, preparation_method, safe_pregnancy, safe_lactation, safe_children, evidence_level, clinical_studies, nursing_notes, monitoring_parameters, scientific_article_url)
VALUES ('Pasiflora', 'Passiflora incarnata', 'Planta con flavonoides que modulan receptores benzodiacepínicos. Ansiolítica y sedante suave.', 'Ansiedad y Nerviosismo', 'Ansiolítico, sedante suave, antiespasmódico', ARRAY['Ansiedad', 'Insomnio', 'Nerviosismo', 'Palpitaciones', 'Estrés', 'Irritabilidad'], ARRAY['ansiedad', 'insomnio', 'nerviosismo', 'palpitaciones'], 'Extracto: 250-500mg 3-4 veces/día. Para insomnio: dosis mayor 1 hora antes dormir. Puede combinarse con valeriana.', 'Puede causar somnolencia. No conducir. Suspender 2 semanas antes cirugías.', 'Ansiedad generalizada, insomnio nervioso, palpitaciones funcionales.', 'Embarazo (efecto uterotónico). Niños <6 años sin supervisión.', 'Somnolencia, mareo. Raramente náuseas, taquicardia paradójica.', 'Potencia sedantes y ansiolíticos. Posible interacción con anticoagulantes.', 'Extracto seco: 250-500mg 3-4 veces/día. Tintura: 2-4ml 3 veces/día.', 'Mayores 6 años: 1/4-1/2 dosis adulto bajo supervisión médica.', ARRAY['oral'], 'Infusión: 2g parte aérea en 200ml agua hirviendo, reposar 10 min.', false, false, false, 'Moderado', 'Akhondzadeh S, et al. J Clin Pharm Ther. 2001;26(5):363-367.', 'Combina bien con valeriana. Advertir sobre somnolencia.', ARRAY['ansiedad', 'calidad sueño', 'frecuencia cardiaca'], 'https://pubmed.ncbi.nlm.nih.gov/11679026/');

-- 14. TILO
INSERT INTO plants (name, scientific_name, description, category, properties, tags, ailments, usage_instructions, warnings, therapeutic_indications, contraindications, side_effects, drug_interactions, dosage_adults, dosage_children, administration_route, preparation_method, safe_pregnancy, safe_lactation, safe_children, evidence_level, clinical_studies, nursing_notes, monitoring_parameters, scientific_article_url)
VALUES ('Tilo', 'Tilia platyphyllos', 'Flores con flavonoides sedantes suaves. Tradicional para nerviosismo e insomnio.', 'Estrés y Tensión Nerviosa', 'Sedante suave, antiespasmódico, diaforético', ARRAY['Estrés', 'Nerviosismo', 'Insomnio', 'Ansiedad', 'Resfriado común', 'Gripe'], ARRAY['nerviosismo', 'insomnio leve', 'ansiedad', 'resfriados'], 'Infusión: 1-2g flores 2-3 veces/día. Para insomnio: taza antes dormir. Bien tolerado, apto familias.', 'Muy seguro. Raramente reacciones alérgicas. No interacciones significativas conocidas.', 'Nerviosismo leve, insomnio de conciliación, resfriados (diaforético).', 'Alergia conocida. Raras contraindicaciones.', 'Muy bien tolerado. Raramente molestias GI leves.', 'Sin interacciones medicamentosas significativas reportadas.', 'Infusión: 1-2g flores 2-3 veces/día.', 'Seguro en niños >1 año: 50-100ml infusión suave.', ARRAY['oral'], 'Infusión: 1-2g flores en 200ml agua 90°C, reposar 10 min tapado.', true, true, true, 'Bajo', 'Toker G, et al. J Ethnopharmacol. 2001;76(3):293-296.', 'Muy seguro, ideal para niños y embarazo. Bien tolerado.', ARRAY['calidad sueño', 'ansiedad'], 'https://pubmed.ncbi.nlm.nih.gov/11448552/');

-- 15. MELISA
INSERT INTO plants (name, scientific_name, description, category, properties, tags, ailments, usage_instructions, warnings, therapeutic_indications, contraindications, side_effects, drug_interactions, dosage_adults, dosage_children, administration_route, preparation_method, safe_pregnancy, safe_lactation, safe_children, evidence_level, clinical_studies, nursing_notes, monitoring_parameters, scientific_article_url)
VALUES ('Melisa', 'Melissa officinalis', 'Hojas con citral y citronelal. Efecto ansiolítico, sedante y antiviral (herpes).', 'Ansiedad e Insomnio Leve', 'Ansiolítico, sedante, antiespasmódico, antiviral', ARRAY['Ansiedad', 'Insomnio', 'Dispepsia', 'Nerviosismo', 'Estrés', 'Heridas superficiales'], ARRAY['ansiedad', 'insomnio', 'herpes labial', 'dispepsia nerviosa'], 'Oral: infusión 2-3 veces/día. Tópico: crema 1-2% para herpes aplicar 4-5 veces/día desde primeros síntomas.', 'Puede potenciar sedantes. No usar tópico en herpes genital sin diagnóstico.', 'Ansiedad leve, insomnio nervioso, dispepsia funcional, herpes labial (tópico).', 'Hipotiroidismo (puede interferir con TSH). Glaucoma (precaución).', 'Bien tolerado. Raramente náuseas, cefalea. Tópico: eritema leve.', 'Puede potenciar sedantes y tiroxina. Precaución con medicamentos tiroideos.', 'Infusión: 1.5-4.5g hojas 3 veces/día. Extracto: 300-500mg 3 veces/día.', 'Mayores 6 años: infusión suave 50-100ml 2 veces/día.', ARRAY['oral', 'topica'], 'Infusión: 2-3g hojas frescas o secas en 200ml agua 85°C, 10 min.', true, true, true, 'Moderado', 'Kennedy DO, et al. Psychosom Med. 2006;68(3):454-458.', 'Efectiva para herpes labial (aplicar desde primeros síntomas).', ARRAY['ansiedad', 'calidad sueño', 'TSH'], 'https://pubmed.ncbi.nlm.nih.gov/16738079/');

